import React,{useReducer}from 'react';
import CartContext from './CartContext';


const initialState = {
    items: [],
    totalAmount:0,
};
const ReducerFunc=(state, action)=>{
    if(action.type === "ADD"){
        const AddMoreItem = state.items.concat(action.item);
        const AddTotalAmount = state.totalAmount + action.item.price * action.item.amount;
        console.log("added",AddTotalAmount);
        return{
            items:AddMoreItem,
            totalAmount:AddTotalAmount,
        }
       
    }
    if(action.type ==="Remove"){
        const RemoveItem = state.items.filter((item) => item)
    }
    return initialState;
}



function CartProvider( props ) {

    const [CartState, dispatchAction]= useReducer(ReducerFunc,initialState)

    const AddHandler=(item)=>{
        dispatchAction({type:"ADD", item:item})
    };
    const removeHandler =(id)=>{
        dispatchAction({type:"REMOVE", id:id})
    };

    const CartContet = {
        item:CartState.items,
        totalAmount:CartState.totalAmount,
        addItem: AddHandler,
        removeItem: removeHandler,
    }
  return (
        <>
        <CartContext.Provider value={CartContet}>
            {props.children}
        </CartContext.Provider>
        </>
  )
}

export default CartProvider
