import React,{useReducer}from 'react';
import CartContext from './CartContext';


const initialState = {
    items: [],
    totalAmount:0,
};
const ReducerFunc=(state, action)=>{
    if(action.type === "ADD"){
        // const AddMoreItem = state.items.concat(action.item);
        const AddTotalAmount = state.totalAmount + action.item.price * action.item.amount;
        const FindCartIndex = state.items.findIndex(item => item.id === action.item.id);
        const existingCartItem = state.items[FindCartIndex];
        
        let UpdateItems;
        if (existingCartItem){
          const UpdateItem ={
            ...existingCartItem,
            amount: existingCartItem.amount + action.item.amount,
           };

           UpdateItems =[...state.items];
           UpdateItems[FindCartIndex] = UpdateItem;
        }else{
            UpdateItems = [...state.items].concat(action.items);
        }
        // console.log("added",AddTotalAmount);
        return{
            items:UpdateItems,
            totalAmount:AddTotalAmount,
        }
       
    }
    // if(action.type ==="Remove"){
       
    //     const RemoveItem = state.items.find((item)=>(item.id === action.id));
    //     const getCurrentAmount = RemoveItem.amount - 1 ;
    //     const AddTotalAmount = state.totalAmount + RemoveItem.price * getCurrentAmount;
    //     return {
    //         item: [{...state.items, amount: getCurrentAmount}],
    //         totalAmount:AddTotalAmount,
    //     }
    // }
    if (action.type === "Remove") {
        const updatedItems = state.items.filter(item => item.id !== action.id);
        console.log(updatedItems);
        const removedItem = state.items.find(item => item.id === action.id);
        const reducedAmount = state.totalAmount - removedItem.price;
      
        return {
          items: updatedItems,
          totalAmount: reducedAmount,
        };
      }
      
    return initialState;
}



function CartProvider( props ) {

    const [CartState, dispatchAction]= useReducer(ReducerFunc,initialState)

    const AddHandler=(item)=>{
        dispatchAction({type:"ADD", item:item})
    };
    const removeHandler =(id)=>{
        dispatchAction({type:"REMOVE", id:id})
    };

    const CartContet = {
        item:CartState.items,
        totalAmount:CartState.totalAmount,
        addItem: AddHandler,
        removeItem: removeHandler,
    }
  return (
        <>
        <CartContext.Provider value={CartContet}>
            {props.children}
        </CartContext.Provider>
        </>
  )
}

export default CartProvider
